pragma solidity ^0.5.0;

import "./ERC721.sol";

/// @title ItemBase contract. Holds all types and events
/// @author Håvard Pedersen
contract ItemContract{

    /*What is required:
        Struct Item
        Struct User
        variable ids
        variable numberOfItems
        Users: Map Address -> User
        ownerOf: Map Item id -> Address
        itemApprovals: Map Item id -> address
        Function BalanceOf
        Function transfer
        Function createItem
        Function deleteItem
        Function UpgradeItem
    */







     /*** EVENTS ***/

    ///@dev mintEvent is called whenever a new item is created. This includes createItem or 
    /// upgradeItem.
    event MintItem(address owner, uint id, uint16 img, uint16[2] stats, uint8 rarity);


    ///@dev In line with ERC721 standards, an event that is fired off any time an item
    /// changes owner. This will also be called right after a new item is created, to assign it
    /// to its rightfull owner.
    ///@param id This is the id of the item that is being transferred.
    event Transfer(address from, address to, uint id);


    ///@dev Delete is an event that is initiated when an item is deleted from address
    event Delete(address from, uint id);





    /*** TYPES ***/

    ///@dev The struct for the item token. Each item is an instance of this struct.
    struct Item {
        uint id;
        uint16 img; // (000 000) = (TYPE INDEX)
        uint16[2] stats; //[Damage/defense, HP/aps]
        uint8 rarity; //1-4 => common, rare, epic, legendary      
        /*
        img type:
        001 - amulet
        002 - helmet
        003 - talisman
        004 - weapon
        005 - body 
        006 - shield
        */
    }


    ///@dev The user struct. Stores Users items an a map of items to index
    struct User{
        Item[] items;
        mapping(uint => uint) indexOfItem;
    }   





    /*** STORAGE ***/
    

    ///@dev stores current highest id of item
    uint ids; 
    

    ///@dev store current amount of items
    uint public numberOfItems;
    

    ///@dev Maps an account address to a user-struct for that user
    mapping(address => User) internal users; 


    ///@dev maps item id to its owner
    mapping(uint => address) public ownerOf;


    ///@dev stores which account address an item is approved for
    mapping(uint => address) public itemApprovals;





    /*** Functions ***/

    ///@dev Returns how many items an account has, given account address
    ///@param _account the account in question
    function balanceOf(address _account) public view returns (uint){
        return users[_account].items.length;
    }
    

    ///@dev Function to return an item given an item
    ///@param _id Id of the item in question
    ///@return returns an array of the stats of the item
    ///@return [id, img, stats1, stats2, rarity]
    function getItem(uint _id) external view
    returns (uint, uint16, uint16, uint16, uint8 ){
        address itemOwner = ownerOf[_id];
        Item memory item = users[itemOwner].items[users[itemOwner].indexOfItem[_id]];
        return (item.id, item.img, item.stats[0], item.stats[1], item.rarity); 
    }


    ///@dev Function to mint a new item, store it in the owners array and send out events.
    ///@param _img the int that stores both item type and logic for what image to use.
    ///@param _stat these two are the stats of the items, where they should be assigned to
    /// stat[] as follows: [_stat1, _stat2].
    ///@param _rarity the rarity of the new item
    ///@param _owner the address of the lucky owner of this newly minted item
    ///@return returns the id of the new item
    function createItem(
        uint16 _img,
        uint16 _stat1, 
        uint16 _stat2, 
        uint8 _rarity,
        address _owner
    ) public returns (uint){
        //Increment highest id in game. 
        //This will be the id of the new item
        ids ++;
        
        //Locally store an item-struct with the given params
        Item memory item = Item(ids, _img, [_stat1, _stat2], _rarity);

        //Push item onto owners list of items and store the index to the item
        users[_owner].indexOfItem[ids] = users[_owner].items.push(item);

        //Set owner of item
        ownerOf[ids] = _owner;

        //Increment how many items currently exists
        numberOfItems++;

        //Trigger mintItem-event to let listeners know new item was created
        emit MintItem(
            _owner,
            ids,
            _img,
            [_stat1, _stat2],
            _rarity
        );
        emit Transfer(address(0), _owner, ids);
        return ids;
    }

    ///@dev This function is used to remove an item from the game
    ///@param _owner this is not strickly necessary, as owner could easily be found.
    /// But passing it as a parameter adds an extra level of security to such a dire function.
    function deleteItem(address _owner, uint _id) public{
        //Check that the address given owns the item
        require(ownerOf[_id] == _owner, "That user dont own the item");

        //remove item from users list of items
        if(balanceOf(_owner) > 1){
            users[_owner].items[users[_owner].indexOfItem[_id]] = users[_owner].items[users[_owner].items.length-1];
        }
        users[_owner].items.length--;

        //remove item from list of item-indexes
        delete users[_owner].indexOfItem[_id];
        
        //Remove map-element in ownerOf
        delete ownerOf[_id];
        
        //Let it be known that an item has been removed
        numberOfItems--;

        //Set of event
        emit Delete(_owner, _id);
    }


    ///@dev this is the function called when upgrading items
    function upgradeItems(address _account, uint _id1, uint _id2, uint _id3) public {
        

        Item memory itemBeingUpgraded = users[_account].items[users[_account].indexOfItem[_id1]];
        
        createItem(            
            itemBeingUpgraded.img,
            itemBeingUpgraded.stats[0] + 50,
            itemBeingUpgraded.stats[1] + 1, 
            itemBeingUpgraded.rarity + 1,
            _account
        );
        deleteItem(_account, _id1);
        deleteItem(_account, _id2);
        deleteItem(_account, _id3);
    }
}
